 val setToUpdate = loggedSetsFlow.first().find { it.id == state.editingSetId }
 if (setToUpdate != null) {
 workoutDao.updateSet(setToUpdate.copy(notes = state.editingNotesText))
 }
 _transientUiState.update { it.copy(editingSetId = null, editingNotesText = "") }
 }
 }

