 if (isSelectionMode) {
 IconButton(onClick = onMoveUp, enabled = isUpEnabled, modifier = Modifier.size(36.dp)) { Icon(Icons.Default.ArrowUpward, "Move Up") }
 IconButton(onClick = onMoveDown, enabled = isDownEnabled, modifier = Modifier.size(36.dp)) { Icon(Icons.Default.ArrowDownward, "Move Down") }
 }
 Box(modifier = Modifier.pointerInput(set.isCompleted) {
 detectTapGestures(
 onTap = { if (!set.isCompleted) onSetChecked() },
 onLongPress = { if (set.isCompleted) onUncheckRequest() }
 )
 }) {
 Checkbox(
