@OptIn(ExperimentalFoundationApi::class)
@Composable
private fun NotesCard(
 isBeingEdited: Boolean,
 editingText: String,
 onEditNotesChange: (String) -> Unit,
 onSaveNote: () -> Unit,
 notes: String,
 onBeginEdit: () -> Unit
) {
 val focusRequester = remember { FocusRequester() }
 var hasFocus by remember { mutableStateOf(false) }

 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(top = 8.dp)
 .clip(CardDefaults.shape)
 .combinedClickable(
 onClick = {},
 onLongClick = onBeginEdit
 ),
 elevation = CardDefaults.cardElevation(defaultElevation = 2.dp)
 ) {
 Column(modifier = Modifier.padding(horizontal = 16.dp, vertical = 8.dp)) {
 if (isBeingEdited) {
 OutlinedTextField(
 value = editingText,
 onValueChange = onEditNotesChange,
 label = { Text("Notes") },
 singleLine = false,
 modifier = Modifier
 .fillMaxWidth()
 .focusRequester(focusRequester)
 .onFocusChanged { focusState ->
 if (hasFocus && !focusState.isFocused) {
 onSaveNote()
 }
 hasFocus = focusState.isFocused
 }
 )
 LaunchedEffect(Unit) { focusRequester.requestFocus() }
 } else if (notes.isNotBlank()) {
 Text(
 text = "Notes: $notes",
 style = MaterialTheme.typography.bodySmall,
 modifier = Modifier.fillMaxWidth()
 )
 } else {
 Text(
 text = "Add a note...",
 style = MaterialTheme.typography.bodySmall,
 modifier = Modifier.fillMaxWidth()
 )
 }
 }
 }
}

@OptIn(ExperimentalLayoutApi::class)
@Composable
private fun SetLogsCard(set: WorkoutSetEntry) {
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(top = 8.dp),
 shape = RoundedCornerShape(8.dp),
 elevation = CardDefaults.cardElevation(defaultElevation = 1.dp),
 colors = CardDefaults.cardColors(containerColor = MaterialTheme.colorScheme.surface)
 ) {
 FlowRow(
 modifier = Modifier
 .padding(horizontal = 8.dp, vertical = 4.dp)
 .fillMaxWidth(),
 horizontalArrangement = Arrangement.SpaceAround,
 verticalArrangement = Arrangement.spacedBy(4.dp)
 ) {
 val duration = (set.exerciseTime ?: 0L) + (set.restTime ?: 0L)
 LogItem("ET", set.exerciseTime?.let { "$it s" } ?: "-")
 LogItem("RT", set.restTime?.let { "$it s" } ?: "-")
 LogItem("D", "${duration / 60}m ${duration % 60}s")
 if (set.isRpeLocked && set.rpe != null) {
 LogItem("RPE", String.format("%.1f", set.rpe))
 }
 val ct = set.completionTime?.let {
 formatTimestamp(it, "HH:mm:ss")
 } ?: "-"
 LogItem("CT", ct)
 }
 }
}

