@OptIn(ExperimentalFoundationApi::class)
@Composable
private fun ExerciseTimerRow(
 workoutExercise: WorkoutExercise?,
 onStartClick: () -> Unit,
 onResetStartRequest: () -> Unit,
 onEndClick: () -> Unit,
 onResetEndRequest: () -> Unit
) {
 Row(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.SpaceAround,
 verticalAlignment = Alignment.CenterVertically
 ) {
 if (workoutExercise?.exerciseStartTime == null) {
 Button(onClick = onStartClick) { Text("Start Exercise") }
 } else {
 Text(
 text = "Started: ${formatTimestamp(workoutExercise.exerciseStartTime, "HH:mm:ss")}",
 modifier = Modifier.combinedClickable(onClick = {}, onLongClick = onResetStartRequest)
 )
 }

 if (workoutExercise?.exerciseEndTime == null) {
 Button(onClick = onEndClick, enabled = workoutExercise?.exerciseStartTime != null) { Text("End Exercise") }
 } else {
 Text(
 text = "Ended: ${formatTimestamp(workoutExercise.exerciseEndTime, "HH:mm:ss")}",
 modifier = Modifier.combinedClickable(onClick = {}, onLongClick = onResetEndRequest)
 )
 }
 }
