 unfocusedTrailingIconColor = LocalContentColor.current
 ),
 modifier = Modifier.fillMaxWidth()
 )
 Spacer(modifier = Modifier.height(4.dp))
 Row(
 horizontalArrangement = Arrangement.spacedBy(8.dp),
 verticalAlignment = Alignment.CenterVertically
 ) {
 OutlinedButton(onClick = onDecrement, modifier = Modifier.size(40.dp), contentPadding = PaddingValues(0.dp), shape = RoundedCornerShape(8.dp)) { Text("-") }
 OutlinedButton(onClick = onIncrement, modifier = Modifier.size(40.dp), contentPadding = PaddingValues(0.dp), shape = RoundedCornerShape(8.dp)) { Text("+") }
 }
