 IconButton(onClick = onMoveDown, enabled = isDownEnabled, modifier = Modifier.size(36.dp)) { Icon(Icons.Default.ArrowDownward, "Move Down") }
 }
 Box(modifier = Modifier.pointerInput(set.isCompleted) {
 detectTapGestures(
