 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(16.dp),
 verticalArrangement = Arrangement.spacedBy(16.dp)
 ) {
 SetParametersCard(uiState = uiState, viewModel = viewModel, isCompact = isCompact)
 ExerciseTimerRow(
 workoutExercise = uiState.workoutExercise,
