@Composable
private fun UnitToggleAllowNoFocus(
 selectedUnit: WeightUnit,
 labelColor: Color,
 onUnitChange: (WeightUnit) -> Unit
) {
 Box(
 contentAlignment = Alignment.Center,
 modifier = Modifier
 .height(56.dp)
 .clip(RoundedCornerShape(4.dp))
 .pointerInput(selectedUnit) {
 detectTapGestures(
 onLongPress = {
 val newUnit =
 if (selectedUnit == WeightUnit.KGS) WeightUnit.LBS else WeightUnit.KGS
 onUnitChange(newUnit)
 },
 onTap = { /* no focus action at all, must tap actual text box */ }
 )
 }
 .padding(horizontal = 8.dp)
 ) {
 Text(
 text = if (selectedUnit == WeightUnit.KGS) "kg" else "lb",
 style = MaterialTheme.typography.bodyMedium,
 fontWeight = FontWeight.Bold,
 color = labelColor
 )
 }
}

@OptIn(ExperimentalLayoutApi::class)
@Composable
private fun SetListItem(
 set: WorkoutSetEntry, isSelected: Boolean, isBeingEdited: Boolean, isSelectionMode: Boolean,
 editingText: String, onEditNotesChange: (String) -> Unit, onSaveNote: () -> Unit,
 onClick: () -> Unit, onLongClick: () -> Unit, onBeginEdit: () -> Unit,
 onMoveUp: () -> Unit, onMoveDown: () -> Unit, isUpEnabled: Boolean, isDownEnabled: Boolean,
 onSetChecked: () -> Unit, onUncheckRequest: () -> Unit,
 onRpeChange: (Float) -> Unit, onLockRpe: () -> Unit, onUnlockRpeRequest: () -> Unit
) {
 val backgroundColor = if (isSelected) MaterialTheme.colorScheme.tertiaryContainer else MaterialTheme.colorScheme.surfaceVariant

 Column {
 // Add parent Card to group all 4 sub-cards with an outline border
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 4.dp),
 shape = RoundedCornerShape(8.dp),
 colors = CardDefaults.cardColors(containerColor = Color.Transparent),
 border = BorderStroke(1.dp, MaterialTheme.colorScheme.outline)
 ) {
 Column(modifier = Modifier.padding(2.dp)) {
 // Card 1: Set Parameter Logs
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .pointerInput(Unit) {
 detectTapGestures(
 onTap = { onClick() },
 onLongPress = { onLongClick() })
 },
 elevation = CardDefaults.cardElevation(defaultElevation = 0.dp),
 colors = CardDefaults.cardColors(containerColor = backgroundColor),
 shape = RoundedCornerShape(6.dp)
 ) {
 Column(modifier = Modifier.padding(horizontal = 12.dp, vertical = 6.dp)) {
 Row(verticalAlignment = Alignment.CenterVertically) {
 val weightInKgs = String.format("%.1f", set.weight / 2.20462)
 Text(
 text = "${set.setNumber}. ${set.weight} lbs | $weightInKgs kgs x ${set.reps} reps",
 style = MaterialTheme.typography.bodyLarge,
 modifier = Modifier.weight(1f)
 )
 Row(verticalAlignment = Alignment.CenterVertically) {
 if (isSelectionMode) {
 IconButton(
 onClick = onMoveUp,
 enabled = isUpEnabled,
 modifier = Modifier.size(28.dp)
 ) { Icon(Icons.Default.ArrowUpward, "Move Up") }
 IconButton(
 onClick = onMoveDown,
 enabled = isDownEnabled,
 modifier = Modifier.size(28.dp)
 ) { Icon(Icons.Default.ArrowDownward, "Move Down") }
 }
 Box(modifier = Modifier.pointerInput(set.isCompleted) {
 detectTapGestures(
 onTap = { if (!set.isCompleted) onSetChecked() },
 onLongPress = { if (set.isCompleted) onUncheckRequest() }
 )
 }) {
 Checkbox(
 checked = set.isCompleted,
 onCheckedChange = null,
 enabled = false,
 colors = CheckboxDefaults.colors(
 disabledCheckedColor = MaterialTheme.colorScheme.primary,
 disabledUncheckedColor = MaterialTheme.colorScheme.onSurfaceVariant
 )
 )
 }
 }
 }
 }
 }
 Spacer(modifier = Modifier.height(1.dp))
 // Card 2: Set RPE Logs
 Card(
 modifier = Modifier
 .fillMaxWidth(),
 elevation = CardDefaults.cardElevation(defaultElevation = 0.dp),
 shape = RoundedCornerShape(6.dp)
 ) {
 Column(modifier = Modifier.padding(horizontal = 12.dp, vertical = 4.dp)) {
 RpeCard(
 set = set,
 onRpeChange = onRpeChange,
 onLockRpe = onLockRpe,
 onUnlockRpeRequest = onUnlockRpeRequest
 )
 }
 }
 Spacer(modifier = Modifier.height(1.dp))
 // Card 3: Set Summary Logs
 Card(
 modifier = Modifier
 .fillMaxWidth(),
 elevation = CardDefaults.cardElevation(defaultElevation = 0.dp),
 shape = RoundedCornerShape(6.dp)
 ) {
 Column(modifier = Modifier.padding(horizontal = 12.dp, vertical = 4.dp)) {
 if (set.isCompleted) {
 SetLogsCard(set = set)
 }
 }
 }
 Spacer(modifier = Modifier.height(2.dp))
 // Card 4: Set Notes
 Card(
 modifier = Modifier
 .fillMaxWidth(),
 elevation = CardDefaults.cardElevation(defaultElevation = 0.dp),
 shape = RoundedCornerShape(6.dp)
 ) {
 Column(
 modifier = Modifier
 .padding(horizontal = 12.dp, vertical = 4.dp)
 .combinedClickable(
 onClick = {},
 onLongClick = onBeginEdit
 )
 ) {
 if (isBeingEdited) {
 val focusRequester = remember { FocusRequester() }
 var hasFocus by remember { mutableStateOf(false) }
 OutlinedTextField(
 value = editingText,
 onValueChange = onEditNotesChange,
 label = { Text("Notes") },
 singleLine = false,
 modifier = Modifier
 .fillMaxWidth()
 .focusRequester(focusRequester)
 .onFocusChanged { focusState ->
 if (hasFocus && !focusState.isFocused) {
 onSaveNote()
 }
 hasFocus = focusState.isFocused
 }
 )
 LaunchedEffect(Unit) { focusRequester.requestFocus() }
 } else if (set.notes.isNotBlank()) {
 Text(
 text = "Notes: ${set.notes}",
 style = MaterialTheme.typography.bodySmall,
 modifier = Modifier.fillMaxWidth()
 )
 } else {
 Text(
 text = "Add a note...",
 style = MaterialTheme.typography.bodySmall,
 modifier = Modifier.fillMaxWidth()
 )
 }
 }
 }
 }
 }
 }
}

@OptIn(ExperimentalLayoutApi::class)
@Composable
private fun SetLogsCard(set: WorkoutSetEntry) {
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(top = 8.dp),
 shape = RoundedCornerShape(8.dp),
 elevation = CardDefaults.cardElevation(defaultElevation = 1.dp),
 colors = CardDefaults.cardColors(containerColor = MaterialTheme.colorScheme.surface)
 ) {
 FlowRow(
 modifier = Modifier
 .padding(horizontal = 8.dp, vertical = 4.dp)
 .fillMaxWidth(),
 horizontalArrangement = Arrangement.SpaceAround,
 verticalArrangement = Arrangement.spacedBy(4.dp)
 ) {
 val duration = (set.exerciseTime ?: 0L) + (set.restTime ?: 0L)
 LogItem("ET", set.exerciseTime?.let { "$it s" } ?: "-")
 LogItem("RT", set.restTime?.let { "$it s" } ?: "-")
 LogItem("D", "${duration / 60}m ${duration % 60}s")
 if (set.isRpeLocked && set.rpe != null) {
 LogItem("RPE", String.format("%.1f", set.rpe))
 }
 val ct = set.completionTime?.let {
 formatTimestamp(it, "HH:mm:ss")
 } ?: "-"
 LogItem("CT", ct)
 }
 }
}

@Composable
private fun LogItem(label: String, value: String) {
 Text(
 text = "$label: $value",
 style = MaterialTheme.typography.bodySmall,
 fontSize = 10.sp
 )
}

