 uiState: WeightExerciseTrackingUiState,
 viewModel: WeightExerciseTrackingViewModel
) {
 val isCompact = widthSizeClass == WindowWidthSizeClass.Compact

 var paramsCardExpanded by remember { mutableStateOf(true) }

 Column(
