@Composable
private fun FloatingLabelOutlinedField(
 value: String,
 onValueChange: (String) -> Unit,
 label: String,
 trailingIcon: @Composable (() -> Unit)? = null,
 modifier: Modifier = Modifier
) {
 var isFocused by remember { mutableStateOf(false) }
 OutlinedTextField(
 value = value,
 onValueChange = onValueChange,
 modifier = modifier.onFocusChanged { isFocused = it.isFocused },
 singleLine = true,
 keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Number),
 label = if (isFocused || value.isNotBlank()) {
 { Text(label) }
 } else null,
 trailingIcon = trailingIcon,
 textStyle = LocalTextStyle.current.copy(textAlign = TextAlign.Center),
 colors = OutlinedTextFieldDefaults.colors(
 focusedTrailingIconColor = LocalContentColor.current,
 unfocusedTrailingIconColor = LocalContentColor.current
 ),
 )
}

@Composable
private fun UnitToggleAllowNoFocus(
 selectedUnit: WeightUnit,
 labelColor: Color,
 onUnitChange: (WeightUnit) -> Unit
) {
 Box(
 contentAlignment = Alignment.Center,
 modifier = Modifier
 .height(56.dp)
 .clip(RoundedCornerShape(4.dp))
 .pointerInput(selectedUnit) {
 detectTapGestures(
 onLongPress = {
 val newUnit =
 if (selectedUnit == WeightUnit.KGS) WeightUnit.LBS else WeightUnit.KGS
 onUnitChange(newUnit)
 },
 onTap = { /* no focus action at all, must tap actual text box */ }
 )
 }
 .padding(horizontal = 8.dp)
 ) {
 Text(
 text = if (selectedUnit == WeightUnit.KGS) "kg" else "lb",
 style = MaterialTheme.typography.bodyMedium,
 fontWeight = FontWeight.Bold,
 color = labelColor
 )
 }
}

