 onValueChange: (String) -> Unit,
 label: String,
 increment: () -> Unit,
 decrement: () -> Unit,
 modifier: Modifier = Modifier,
 trailingIcon: (@Composable () -> Unit)? = null,
 labelColor: Color
) {
