@Composable
private fun SetListItem(
 set: WorkoutSetEntry,
 isSelected: Boolean,
 isBeingEdited: Boolean,
 isSelectionMode: Boolean,
 editingText: String,
 onEditNotesChange: (String) -> Unit,
 onSaveNote: () -> Unit,
 onClick: () -> Unit,
 onLongClick: () -> Unit,
 onBeginEdit: () -> Unit,
 onMoveUp: () -> Unit,
 onMoveDown: () -> Unit,
 isUpEnabled: Boolean,
 isDownEnabled: Boolean,
 onSetChecked: () -> Unit,
 onUncheckRequest: () -> Unit,
 onRpeChange: (Float) -> Unit,
 onLockRpe: () -> Unit,
 onUnlockRpeRequest: () -> Unit
) {
