 )
 Spacer(modifier = Modifier.height(8.dp))
 LazyColumn(verticalArrangement = Arrangement.spacedBy(8.dp)) {
 items(uiState.loggedSets.size, key = { uiState.loggedSets[it].id }) { index ->
 val set = uiState.loggedSets[index]
